    private void jMenuItemNormalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemNormalActionPerformed
        // TODO add your handling code here:
        this.setContentPane(pLista);
        pLista.actualizaLista(lista);
        pLista.iniBotones();
        
        if(!lista.vacia()) //SI LA LISTA NO ESTA VACIA, MUESTRA LOS DATOS DEL PRIMERO
            pLista.setJTextField(lista);
        
        pack();
    }//GEN-LAST:event_jMenuItemNormalActionPerformed

