    private void creaObjeto(){ //CREAR OBJETOS INICIALES EN LA LISTA
        
        Empleado emp1 = new Empleado("Pepe", 1500, 2000, "10/10/2010");
        lista.crearNodo(emp1, emp1.getNumero());
        Empleado emp2 = new Empleado("Rocio", 2000, 2500, "19/09/2022");
        lista.crearNodo(emp2, emp2.getNumero());
        Empleado emp3 = new Empleado("Lola", (float) 900.75, 1000, "10/10/2010");
        lista.crearNodo(emp3, emp3.getNumero());
        Empleado emp4 = new Empleado("Juan", (float) 1800.5, 1900, "19/09/2022");
        lista.crearNodo(emp4, emp4.getNumero());   
    }
    
